import React from 'react';
import { useState } from "react";
import '../../../node_modules/font-awesome/css/font-awesome.min.css'; 
import { default as axios} from 'axios';


export const hashtags = "css #git #github #frontend #reactjs #javascript #coding #programming #khattakdev #crash #course #development #web #technologies #typescript #chrome #dev #python #html #programmer #developer #code #coder #php #software #computerscience #webdeveloper #webdevelopment #softwaredeveloper #codinglife #technology #linux #webdesign #android #thesmartcoders #programmingmemes #tech #programmers #c #softwareengineer #computer #java #pythonhub #python3 #pythonprogramming #ai #ml #artificialintelligence #machinelearning #pythonquiz #react #developers #learncoding #webdev #coders #programmingstudents #programmers #htmlcss #100daysofcode #javascriptdeveloper #learnjavascript #csstricks #nodejs";
const access_token = "EAAJDv7yPPgQBAKaOMmfuxuqifoGwvow4T0Tre3bkG4PVJYfZAlQofC91fHLQCB5aQVJ26pSDLdDMqyQ79HcEIWLAKVPeSRjcabFXr3MyXmtTS9d2ZA5zkN5Ha6CadeDA4YTMDy6JH5Jtfwm52e4ILMczq8syXIwLxfgtFu4Qpe8klI22OfGX6pmIfXe8B1dHNALhXvNwZDZD"

const Search = () => {
    const [hashtags, setHashtag] = useState("");
    return (

        <div className='p-4 mx-5 d-flex row justify-content-center'> 
            <div className="col-1"></div>
            <div className='col-7'>            
                <input type="search" id="searchBar" className="form-control rounded" placeholder="Search"/>
            </div>
            <div className="col-1">
            <button type="button" className="btn btn-dark mx-1" onclick={searchApi(document.getElementById("searchBar").value)}>
                <i className="fa px-1 fa-search fax-3"></i>
            </button>
            </div>
        </div>
    )
}

function onEnter(){
    const searchBar = document.getElementById("searchBar");
    console.log("asfdsdaf")
    searchBar.addEventListener("keypress", function(event) {
        if (1) {
            event.preventDefault();
            console.log("qwer");
            document.getElementById("searchButton").click();
        }
    }); 
}


async function getHashtags(hashtag)
{
    await axios.get(`https://graph.facebook.com/v15.0/ig_hashtag_search?user_id=17841451359932261&q=${hashtag}&access_token=${access_token}`)
      .then(response => {
            id = response.data.data[0].id;
          getAllHashtags(id);
      })
      .catch(error => {
          console.log(error);
      });

}


async function getAllHashtags(id)
{
    let hashtags = []
    await axios.get(`https://graph.facebook.com/v15.0/${id}/top_media?user_id=17841451359932261&limit=5&fields=caption&access_token=${access_token}`)
      .then(response => {
          const data = response.data.data;

          for(let i=0;i<data.length;i++)
          {
              let caption = data[i].caption;
              let words = caption.split(' ')
              for(let j=0;j<words.length;j++)
              {
                if(words[j].startsWith("#"))
                      hashtags.push(words[j])
              }
          }
          hashtags = removeDuplicates(hashtags)
          doSomething(hashtags);
      })
      .catch(error => {
          console.log(error);
      });


}

function doSomething(hashtags)
{
    const hashtagsP = document.getElementById("hashtags");
    let allHashtags = "";

    for(let i=0;i<hashtags.length;i++)
    {
        allHashtags = allHashtags + hashtags[i].trim()+ " ";
    }
    hashtagsP.innerText =  allHashtags;
}

function removeDuplicates(arr) {
        return [...new Set(arr)];
}

function searchApi(){
    const searchBar = document.getElementById("searchBar");

    var searchItem = searchBar.value;
    console.log(searchItem);

    getHashtags(searchItem);

}


export default Search;
