import React from 'react';
import { useState } from "react";
import '../../../node_modules/font-awesome/css/font-awesome.min.css'; 
import './Search.css';
import {default as axios} from 'axios';
import ClipLoader from "react-spinners/ClipLoader";

const apiUrl = "http://localhost:5000/getHashtags";

const Search = () => {
    const [hashtags, setState] = useState("#git #github #frontend #reactjs #javascript #coding #programming #khattakdev #crash #course #development #web #technologies #typescript #chrome #dev #python #html #programmer #developer #code #coder #php #software #computerscience #webdeveloper #webdevelopment #softwaredeveloper #codinglife #technology #linux #webdesign #android #thesmartcoders #programmingmemes #tech #programmers #c #softwareengineer #computer #java #pythonhub #python3 #pythonprogramming #ai #ml #artificialintelligence #machinelearning #pythonquiz #react #developers #learncoding #webdev #coders #programmingstudents #programmers #htmlcss #100daysofcode #javascriptdeveloper #learnjavascript #csstricks #nodejs")  
    let [loadingInProgress, setLoading] = useState(true);
    const spinnerTag = document.getElementById("spinner");
    spinnerTag.style.display = "none";
    return (
        <section>

            <div className='py-4 ps-5 ms-5 container-lg row justify-content-center'> 
                <div className="col-1"></div>
                <div className='col-7'>            
                    <input type="search" id="searchBar" autoComplete="on" className="form-control rounded" placeholder="Search"/>
                </div>
                <div className="col-1">
                <button type="button" className="btn btn-dark mx-1" id="searchButton" onClick={()=>{
                    const searchHashtag = document.getElementById("searchBar").value;
                    const hashtagP = document.getElementById("allHashtags");
                    const displayArea = document.getElementById("displayArea");

                    console.log(spinnerTag);
                    hashtagP.style.display = "none";
                    displayArea.style.textAlign = "center";
                    spinnerTag.style.display = "block";
                    let hashSearch = "?hashtag=" + searchHashtag;
                    if(searchHashtag.length>0)
                    {
                        axios.get(apiUrl+hashSearch)
                        .then(response => {
                            spinnerTag.style.display = "none";
                            hashtagP.style.display = "block";
                            hashtagP.style.alignSelf = "left";
                            setState(response.data);
                        })
                    }
                    else{
                        setState("#git #github #frontend #reactjs #javascript #coding #programming #khattakdev #crash #course #development #web #technologies #typescript #chrome #dev #python #html #programmer #developer #code #coder #php #software #computerscience #webdeveloper #webdevelopment #softwaredeveloper #codinglife #technology #linux #webdesign #android #thesmartcoders #programmingmemes #tech #programmers #c #softwareengineer #computer #java #pythonhub #python3 #pythonprogramming #ai #ml #artificialintelligence #machinelearning #pythonquiz #react #developers #learncoding #webdev #coders #programmingstudents #programmers #htmlcss #100daysofcode #javascriptdeveloper #learnjavascript #csstricks #nodejs")
                    }
                }}>
                    <i className="fa px-1 fa-search fax-5"></i>
                </button>
                </div>

                <div className="col-1">
                <button type="button" className="btn btn-dark mx-1" id="copyButton" onClick={()=>{
                        navigator.clipboard.writeText(hashtags);
                }}>
                    <i className="fa fa-clone fax-4"></i>
                </button>
                </div>


            </div>

            <div className="row d-flex justify-content-center">
                <div className="bg-light col-7 m-4" id="displayArea">
                    <ClipLoader color={'black'} loading={loadingInProgress} size={150} id="spinner"/>
                    <p className='p' id="allHashtags">{hashtags}</p>
                </div>
            </div>
            <div className="container">

    </div>
        </section>

    )
}


export default Search;
