import React from 'react';
import { useState } from "react";
import '../../../node_modules/font-awesome/css/font-awesome.min.css'; 
import './Search.css'
import {default as axios} from 'axios';
import { Spinner, Button } from "react-bootstrap";

const apiUrl = "http://localhost:5000/getHashtags";


const Search = () => {
    const [hashtags, setState] = useState("#git #github #frontend #reactjs #javascript #coding #programming #khattakdev #crash #course #development #web #technologies #typescript #chrome #dev #python #html #programmer #developer #code #coder #php #software #computerscience #webdeveloper #webdevelopment #softwaredeveloper #codinglife #technology #linux #webdesign #android #thesmartcoders #programmingmemes #tech #programmers #c #softwareengineer #computer #java #pythonhub #python3 #pythonprogramming #ai #ml #artificialintelligence #machinelearning #pythonquiz #react #developers #learncoding #webdev #coders #programmingstudents #programmers #htmlcss #100daysofcode #javascriptdeveloper #learnjavascript #csstricks #nodejs")  
    const [spinner, setSpinner] = useState(false);
    return (
        <section>

            <div className='p-4 mx-5 d-flex row justify-content-center'> 
                <div className="col-1"></div>
                <div className='col-7'>            
                    <input type="search" id="searchBar" className="form-control rounded" placeholder="Search"/>
                </div>
                <div className="col-1">
                <button type="button" className="btn btn-dark mx-1" id="searchButton" onClick={()=>{
                    {spinner && (
                        <p>something is loading</p>
                    )}
                    setSpinner(true);
                    const searchHashtag = document.getElementById("searchBar").value;
                    let hashSearch = "?hashtag=" + searchHashtag;
                    axios.get(apiUrl+hashSearch)
                    .then(response => {
                        setSpinner(false);
                        setState(response.data);
                    })
                }}>
                    <i className="fa px-1 fa-search fax-3"></i>
                </button>
                </div>
            </div>

            <div className="row d-flex justify-content-center">
                <div className="bg-light  displayArea col-5 m-4">
                    <p className='p'>{hashtags}</p>
                </div>
            </div>
        </section>

    )
}


export default Search;
